guardrails.checkScript:
param($Ctx, $Threshold, $ApiCall)
$r = $ApiCall.Invoke('/api/v2/routing/queues?pageSize=100')
$test = @($r.entities | Where-Object { $_.name -like 'TEST*' })
if ($test.Count -gt 0) {
  return @{ Pass=$false; Detail="$($test.Count) TEST queue(s) found: $(($test.name) -join ', ')" }
}
return $true

param($Ctx, $Threshold, $ApiCall)
$r = $ApiCall.Invoke('/api/v2/routing/queues?pageSize=100')
$over = @($r.entities | Where-Object { $_.acwSettings -and $_.acwSettings.timeoutMs -gt $Threshold })
if ($over.Count -gt 0) {
  return @{ Pass=$false; Detail="$($over.Count) queue(s) exceed ACW limit" }
}
return $true
